import numpy as np


def solve(objective_function, config, prev_best_x=None):
    """
    Blackbox minimization with hybrid global/local search + CMA-like refinement.

    This version focuses on:
    - Simpler and more reliable budget allocation
    - Deterministic use of the whole remaining budget
    - Slightly stronger global exploration (including opposite points)
    - Robust handling of degenerate dimensions and weird objective behavior
    """

    bounds = np.asarray(config["bounds"], dtype=float)
    dim = int(config["dim"])
    budget = int(config["budget"])

    # Basic sanity fallbacks
    if dim <= 0 or budget <= 0 or bounds.size == 0:
        if bounds.size == 0:
            return np.zeros(dim, dtype=float)
        low = bounds[:, 0]
        high = bounds[:, 1]
        return (low + high) / 2.0

    seed = config.get("seed", None)
    rng = np.random.RandomState(seed) if seed is not None else np.random

    low = bounds[:, 0].astype(float)
    high = bounds[:, 1].astype(float)
    span = high - low
    zero_mask = span == 0.0
    span_safe = span.copy()
    span_safe[zero_mask] = 1.0

    def clip(x):
        return np.minimum(np.maximum(x, low), high)

    best_x = None
    best_y = np.inf
    evals = 0

    def safe_eval(x):
        nonlocal evals, best_x, best_y
        if evals >= budget:
            return np.inf
        try:
            y = objective_function(x)
        except Exception:
            y = np.inf
        evals += 1
        if np.isfinite(y) and y < best_y:
            best_x, best_y = x.copy(), float(y)
        return y

    # ----------------- Warm start from prev_best_x -----------------
    if prev_best_x is not None and evals < budget:
        try:
            x0 = np.asarray(prev_best_x, dtype=float).reshape(-1)
            if x0.size == dim and np.all(np.isfinite(x0)):
                x0 = clip(x0)
                y0 = safe_eval(x0)
                if np.isfinite(y0) and evals < budget:
                    jitter_scale = 0.05 * span_safe
                    jitter_scale[zero_mask] = 0.1
                    n_jitter = min(max(dim, 4), max(1, budget // 15))
                    n_jitter = min(n_jitter, budget - evals)
                    for _ in range(n_jitter):
                        if evals >= budget:
                            break
                        cand = x0 + rng.uniform(-jitter_scale, jitter_scale)
                        cand = clip(cand)
                        safe_eval(cand)
        except Exception:
            pass

    # Fallback initial point if needed
    if (best_x is None or not np.isfinite(best_y)) and evals < budget:
        x0 = low + span_safe * rng.rand(dim)
        y0 = safe_eval(x0)
        if not np.isfinite(y0):
            best_x, best_y = x0.copy(), np.inf

    if best_x is None or evals >= budget:
        return np.asarray(
            best_x if best_x is not None else (low + high) / 2.0, dtype=float
        )

    remaining_total = budget - evals
    if remaining_total <= 0:
        return np.asarray(best_x, dtype=float)

    # ----------------- Low-discrepancy style warmup -----------------
    # Provide diverse coverage early when budget allows
    if remaining_total >= max(6, 2 * dim):
        warmup_evals = min(max(4, 2 * dim), remaining_total // 3)
        warmup_evals = max(0, min(warmup_evals, budget - evals))

        def halton_index(i, base):
            f = 1.0
            r = 0.0
            while i > 0:
                f *= 1.0 / base
                r += f * (i % base)
                i //= base
            return r

        bases = [2, 3, 5, 7, 11, 13, 17, 19]
        while len(bases) < dim:
            bases.append(bases[-1] + 2)

        for i in range(1, warmup_evals + 1):
            if evals >= budget:
                break
            u = np.empty(dim)
            for d in range(dim):
                u[d] = halton_index(i + rng.randint(0, 1024), bases[d])
            u = np.mod(u, 1.0)
            x = low + span_safe * u
            # Mild bias toward current best
            if rng.rand() < 0.4 and best_x is not None:
                center = (low + high) * 0.5
                scale = 0.25
                x = best_x + (x - center) * scale
                x = clip(x)
            safe_eval(x)

    remaining_total = budget - evals
    if remaining_total <= 0 or best_x is None:
        return np.asarray(best_x, dtype=float)

    # ----------------- Budget split: global / local / CMA-like -----------------
    if dim <= 3:
        base_global_frac = 0.45
    elif dim <= 8:
        base_global_frac = 0.6
    elif dim <= 20:
        base_global_frac = 0.7
    else:
        base_global_frac = 0.75

    if remaining_total <= 25:
        global_frac = min(0.9, base_global_frac + 0.15)
        cma_frac = 0.0
    elif remaining_total <= 80:
        global_frac = base_global_frac
        cma_frac = 0.15
    else:
        global_frac = base_global_frac
        cma_frac = 0.2

    global_frac = float(np.clip(global_frac, 0.3, 0.9))
    cma_evals = int(round(cma_frac * remaining_total))
    cma_evals = min(cma_evals, remaining_total // 2)
    cma_evals = max(0, min(cma_evals, budget - evals))

    remaining = budget - evals - cma_evals
    if remaining < 0:
        remaining = 0
        cma_evals = max(0, budget - evals)

    n_global = int(round(global_frac * remaining))
    n_global = max(1, min(n_global, remaining))
    n_local = max(0, remaining - n_global)

    # Guarantee at least a small local phase when possible
    if n_local == 0 and remaining >= max(6, 2 * dim):
        n_local = min(remaining // 4, max(4, dim))
        n_global = max(1, remaining - n_local)

    # ----------------- Global search -----------------
    if n_global > 0 and evals < budget:
        if n_global >= dim + 2:
            base_linspace = np.linspace(0.0, 1.0, n_global, endpoint=False)
            jitter = rng.rand(n_global) / n_global
            strata = base_linspace + jitter
            perms = [rng.permutation(n_global) for _ in range(dim)]

            for i in range(n_global):
                if evals >= budget:
                    break
                use_neighborhood = best_x is not None and rng.rand() < 0.55
                if use_neighborhood:
                    progress = evals / float(budget)
                    scale = 0.3 * (1.0 - 0.6 * progress)
                    scale = float(np.clip(scale, 0.06, 0.35))
                    step = scale * span_safe
                    cand = best_x + rng.uniform(-step, step)
                    x = clip(cand)
                else:
                    u = np.empty(dim)
                    for d in range(dim):
                        u[d] = strata[perms[d][i]]
                    x = low + span_safe * u
                y = safe_eval(x)
                if evals < budget and rng.rand() < 0.15 and np.isfinite(y):
                    x_opp = low + high - x
                    x_opp = clip(x_opp)
                    if not np.allclose(x_opp, x, atol=1e-12):
                        safe_eval(x_opp)
        else:
            for _ in range(n_global):
                if evals >= budget:
                    break
                if best_x is not None and rng.rand() < 0.6:
                    step = 0.35 * span_safe
                    u = rng.rand(dim)
                    perturb = step * np.tan(np.pi * (u - 0.5))
                    max_step = 1.5 * span_safe
                    perturb = np.clip(perturb, -max_step, max_step)
                    cand = best_x + perturb
                    x = clip(cand)
                else:
                    x = low + span_safe * rng.rand(dim)
                safe_eval(x)

    if best_x is None or evals >= budget:
        return np.asarray(
            best_x if best_x is not None else (low + high) / 2.0, dtype=float
        )

    # ----------------- Local search -----------------
    remaining_after_global = budget - evals
    if remaining_after_global <= 0:
        return np.asarray(best_x, dtype=float)

    cma_budget = min(cma_evals, max(0, remaining_after_global - n_local))
    n_local = min(n_local, max(0, budget - evals - cma_budget))

    if n_local > 0 and evals < budget:
        base_step = 0.12 * span_safe
        base_step[zero_mask] = 1.0

        passes = min(20, max(1, n_local // max(1, dim)))
        evals_per_pass = max(1, n_local // passes)

        current_x = best_x.copy()
        current_y = best_y

        for p in range(passes):
            if evals >= budget:
                break
            step_scale = max(0.01, 0.65 ** (p + 1))
            step = base_step * step_scale
            for _ in range(evals_per_pass):
                if evals >= budget:
                    break

                if dim <= 3:
                    k = 1
                elif dim <= 10:
                    k = rng.randint(1, 3)
                else:
                    k = rng.randint(1, max(2, dim // 4))

                idx = rng.choice(dim, size=k, replace=False)
                if rng.rand() < 0.25:
                    local_step = step * 2.0
                else:
                    local_step = step

                candidate = current_x.copy()
                perturb = rng.uniform(-local_step[idx], local_step[idx])
                candidate[idx] += perturb
                candidate = clip(candidate)

                y = safe_eval(candidate)
                if not np.isfinite(y):
                    continue
                if y < current_y:
                    current_x, current_y = candidate, y
                else:
                    if rng.rand() < 0.02:
                        current_x, current_y = candidate, y

        if current_y < best_y:
            best_x, best_y = current_x.copy(), current_y

    if evals >= budget:
        return np.asarray(
            best_x if best_x is not None else (low + high) / 2.0, dtype=float
        )

    # ----------------- CMA-like refinement -----------------
    remaining_for_cma = min(cma_budget, budget - evals)
    if remaining_for_cma <= 0 or best_x is None:
        return np.asarray(best_x, dtype=float)

    mean = best_x.copy()
    sigma = 0.08 * span_safe
    sigma[zero_mask] = 0.5

    lam = max(4, 2 * dim)
    lam = min(lam, max(4, remaining_for_cma))
    iterations = max(1, remaining_for_cma // lam)

    if iterations <= 0 or lam <= 0:
        return np.asarray(best_x, dtype=float)

    stagnation_counter = 0
    no_improve_iters = 0

    for _ in range(iterations):
        if evals >= budget:
            break

        pop = mean + sigma * rng.randn(lam, dim)
        pop = np.clip(pop, low, high)

        ys = np.empty(lam, dtype=float)
        valid_count = 0
        any_finite = False

        for i in range(lam):
            if evals >= budget:
                break
            y = safe_eval(pop[i])
            ys[valid_count] = y
            pop[valid_count] = pop[i]
            valid_count += 1
            if np.isfinite(y):
                any_finite = True

        if valid_count == 0 or not any_finite:
            no_improve_iters += 1
            if no_improve_iters >= 2:
                sigma *= 1.5
                sigma = np.maximum(sigma, 1e-8 * span_safe)
            continue

        ys = ys[:valid_count]
        pop = pop[:valid_count]

        finite_mask = np.isfinite(ys)
        if not np.any(finite_mask):
            no_improve_iters += 1
            if no_improve_iters >= 2:
                sigma *= 1.5
                sigma = np.maximum(sigma, 1e-8 * span_safe)
            continue

        ys_f = ys[finite_mask]
        pop_f = pop[finite_mask]

        idx_sorted = np.argsort(ys_f)
        pop_sorted = pop_f[idx_sorted]
        ys_sorted = ys_f[idx_sorted]

        mu = max(2, lam // 2)
        mu = min(mu, pop_sorted.shape[0])
        elites = pop_sorted[:mu]

        weights = np.log(mu + 0.5) - np.log(np.arange(1, mu + 1))
        weights = weights / np.sum(weights)
        new_mean = np.sum(elites * weights[:, None], axis=0)

        best_iter = ys_sorted[0]

        if np.isfinite(best_iter) and best_iter <= best_y + 1e-12:
            sigma *= 0.85
            stagnation_counter += 1
        else:
            sigma *= 1.05
            stagnation_counter = 0

        sigma = np.maximum(sigma, 1e-8 * span_safe)
        mean = 0.6 * mean + 0.4 * new_mean

        if np.isfinite(best_iter) and best_iter < best_y:
            best_y = float(best_iter)
            best_x = pop_sorted[0].copy()
            no_improve_iters = 0
        else:
            no_improve_iters += 1

        if stagnation_counter >= 3:
            stagnation_counter = 0
            sigma = 0.06 * span_safe
            if best_x is not None:
                mean = best_x.copy()
            else:
                mean = low + span_safe * rng.rand(dim)

        if no_improve_iters >= 4:
            no_improve_iters = 0
            mean = low + span_safe * rng.rand(dim)
            sigma = 0.1 * span_safe
            sigma[zero_mask] = 0.5

    if best_x is None:
        best_x = np.clip(mean, low, high)

    return np.asarray(best_x, dtype=float)
