import numpy as np

def solve(objective_function, config, prev_best_x=None):
    bounds = np.array(config['bounds'], dtype=float)
    dim = int(config.get('dim', bounds.shape[0]))
    budget = int(config.get('budget', 1))

    low = bounds[:, 0]
    high = bounds[:, 1]
    span = high - low
    span_safe = np.where(span > 0, span, 1.0)

    rng = np.random.default_rng()

    def clip(x):
        return np.clip(x, low, high)

    evals_used = 0
    best_x = None
    best_y = np.inf

    def eval_point(x):
        nonlocal evals_used, best_x, best_y
        if evals_used >= budget:
            return None
        x = np.asarray(x, dtype=float).reshape(dim,)
        x = clip(x)
        y = objective_function(x)
        evals_used += 1
        if y < best_y:
            best_y = y
            best_x = x.copy()
        return y

    # Early exit if no budget
    if budget <= 0:
        if prev_best_x is not None:
            x0 = np.asarray(prev_best_x, dtype=float).reshape(dim,)
            return clip(x0)
        return clip(rng.uniform(low, high))

    # 1) Warm start if available
    if prev_best_x is not None:
        x0 = np.asarray(prev_best_x, dtype=float).reshape(dim,)
        eval_point(x0)
        if evals_used >= budget:
            return best_x if best_x is not None else clip(x0)

    if evals_used >= budget:
        if best_x is None:
            best_x = clip(rng.uniform(low, high))
        return best_x

    remaining = budget - evals_used
    if remaining <= 0:
        if best_x is None:
            best_x = clip(rng.uniform(low, high))
        return best_x

    # 2) GLOBAL EXPLORATION: keep but make slightly more robust
    if dim <= 5:
        global_frac = 0.35
    elif dim <= 20:
        global_frac = 0.45
    else:
        global_frac = 0.6

    n_global = max(1, int(global_frac * remaining))
    n_global = min(n_global, remaining)

    if n_global > 0:
        samples = rng.random((n_global, dim))
        for d in range(dim):
            rng.shuffle(samples[:, d])
        X_global = low + samples * span_safe

        for i in range(n_global):
            if evals_used >= budget:
                break
            eval_point(X_global[i])

    if evals_used >= budget:
        return best_x if best_x is not None else clip(rng.uniform(low, high))

    if best_x is None:
        x_init = clip(rng.uniform(low, high))
        eval_point(x_init)
        if evals_used >= budget:
            return best_x if best_x is not None else x_init

    # 3) EVOLUTION STRATEGY AROUND BEST (CMA-lite / NES-style) – tuned for rugged landscapes
    remaining = budget - evals_used
    if remaining <= 0:
        return best_x

    if dim <= 5:
        base_pop = 18
    elif dim <= 20:
        base_pop = 30
    else:
        base_pop = 40

    max_generations_target = 14
    base_pop = min(base_pop, max(4, remaining // max(1, max_generations_target)))
    pop_size = max(4, base_pop)

    elite_frac = 0.25
    n_elite = max(1, int(elite_frac * pop_size))

    # Let ES use a bit more of remaining budget; keep room for local search
    es_budget = int(0.8 * remaining)
    es_budget = max(pop_size * 3, es_budget)
    es_budget = min(es_budget, remaining)

    # Track global mean for restart; Schwefel-like functions may benefit from broader search
    if es_budget > 0 and pop_size > 1:
        mean = best_x.copy()

        initial_sigma = 0.8 * span_safe
        sigma = np.minimum(initial_sigma, span_safe)
        min_sigma = 1e-6 * span_safe
        max_sigma = span_safe

        no_improve_iters = 0
        last_best_y = best_y
        es_iters = 0

        while evals_used < budget and es_budget > 0:
            cur_pop = min(pop_size, es_budget, budget - evals_used)
            if cur_pop <= 0:
                break

            half = (cur_pop + 1) // 2
            z = rng.standard_normal((half, dim))
            z = np.vstack([z, -z])[:cur_pop]
            offspring = mean + sigma * z
            offspring = clip(offspring)

            ys = []
            for i in range(cur_pop):
                if evals_used >= budget or es_budget <= 0:
                    break
                y = eval_point(offspring[i])
                es_budget -= 1
                if y is not None:
                    ys.append((y, offspring[i].copy()))

            if evals_used >= budget or len(ys) == 0:
                break

            ys.sort(key=lambda t: t[0])
            elites = np.array([t[1] for t in ys[:n_elite]])

            new_mean = elites.mean(axis=0)
            elite_spread = np.std(elites - new_mean, axis=0)

            # Slightly more conservative sigma update to avoid premature shrinking
            sigma = 0.5 * sigma + 0.5 * np.maximum(elite_spread, min_sigma)
            sigma = np.clip(sigma, min_sigma, max_sigma)
            mean = new_mean
            es_iters += 1

            if best_y < last_best_y - 1e-12:
                last_best_y = best_y
                no_improve_iters = 0
            else:
                no_improve_iters += 1

            # Diversification on stagnation: use global random restarts in addition to local jumps
            if no_improve_iters >= 4:
                # A few random global probes
                n_global_jumps = min(6, es_budget)
                if n_global_jumps > 0:
                    rand_points = low + rng.random((n_global_jumps, dim)) * span_safe
                    for j in range(n_global_jumps):
                        if evals_used >= budget or es_budget <= 0:
                            break
                        y = eval_point(rand_points[j])
                        es_budget -= 1

                # Jumps around current best solution
                jump_radius = 0.6 * span_safe
                n_jumps = 3
                for _ in range(n_jumps):
                    if evals_used >= budget or es_budget <= 0:
                        break
                    cand = best_x + rng.uniform(-jump_radius, jump_radius, size=dim)
                    cand = clip(cand)
                    y = eval_point(cand)
                    es_budget -= 1

                # Re-center mean on best_x to exploit any new basin
                mean = best_x.copy()
                sigma = np.minimum(2.5 * sigma, max_sigma)
                no_improve_iters = 0

            # Occasionally re-expand sigma if search collapsed too soon
            if np.all(sigma < 3e-7 * span_safe) and es_iters < 5 and es_budget > pop_size:
                sigma = np.maximum(0.15 * span_safe, min_sigma)

    if evals_used >= budget:
        return best_x

    # 4) LOCAL SEARCH: adaptive coordinate search – slightly more exploitative
    remaining = budget - evals_used
    if remaining <= 0 or best_x is None:
        if best_x is None:
            best_x = clip(rng.uniform(low, high))
        return best_x

    x = best_x.copy()

    step = 0.08 * span_safe
    step = np.minimum(step, 0.25 * span_safe)
    step[span == 0] = 0.0

    if dim > 0:
        max_iters = max(1, remaining // (2 * dim))
    else:
        max_iters = 1

    no_improve_iters = 0

    for _ in range(max_iters):
        if evals_used >= budget:
            break
        improved = False

        coords = np.arange(dim)
        rng.shuffle(coords)

        for d in coords:
            if evals_used >= budget:
                break
            if step[d] == 0.0:
                continue

            x_pos = x.copy()
            x_pos[d] = x[d] + step[d]
            x_pos = clip(x_pos)
            y_pos = eval_point(x_pos)

            if evals_used >= budget:
                break

            if y_pos is not None and y_pos < best_y:
                x = x_pos
                improved = True
                continue

            x_neg = x.copy()
            x_neg[d] = x[d] - step[d]
            x_neg = clip(x_neg)
            y_neg = eval_point(x_neg)

            if y_neg is not None and y_neg < best_y:
                x = x_neg
                improved = True

            if evals_used >= budget:
                break

        if evals_used >= budget:
            break

        if not improved:
            step *= 0.5
            no_improve_iters += 1
            if np.all(step < 1e-7 * span_safe):
                break

            if no_improve_iters >= 3 and evals_used < budget:
                # Occasionally restart more globally to escape deceptive basins
                if rng.random() < 0.4:
                    x_restart = low + rng.random(dim) * span_safe
                else:
                    radius = np.maximum(0.15 * span_safe, step)
                    noise = rng.uniform(-radius, radius, size=dim)
                    x_restart = clip(best_x + noise)
                y_restart = eval_point(x_restart)
                if y_restart is not None and y_restart < best_y:
                    x = x_restart
                no_improve_iters = 0
        else:
            no_improve_iters = 0
            step *= 1.1
            step = np.minimum(step, 0.25 * span_safe)

    if best_x is None:
        best_x = clip(rng.uniform(low, high))
    return best_x
